import React, { useState, useEffect } from "react";
import "./WeatherInfo.scss";
import { geolocation } from "react-geolocated";


const WeatherInfo = () => {
    //defining state with "empty" object to begin with
    const [weatherStats, setWeatherStats] = useState({
        city: [{ location: { name: "" } }],
        tempCelius: [{ currrent: { temp_c: "" } }],
        tempFarenheit: [{ currrent: { temp_f: "" } }],
        conditionText: [{ condition: { text: "" } }],
        conditionIcon: [{ condition: { icon: "" } }]
    });

    const getWeatherStats = () => {
        // fetch("http://api.weatherapi.com/v1/current.json?key=e38763e617664e56ab8162340220102&q=London&aqi=no", { mode: 'no-cors'})
        fetch("http://api.weatherapi.com/v1/current.json?key=e38763e617664e56ab8162340220102&q=London&aqi=no")
            .then((response) => response.json()) // returning response turned to json which also returns a promise
            .then((jsonResponse) => setWeatherStats(jsonResponse)) // taking the jsonified respose from last .then() and setting it to the local state
            .catch((err) => console.log(err)); // error handling
            console.log(weatherStats.location.name);
    }

    useEffect(() => {
        getWeatherStats();
    });

    return (
        <div>
            <p className="tweet__text">{weatherStats ? weatherStats.location.lat : ""}</p>
            {/* <p className="tweet__text">{weatherStats ? weatherStats.current.temp_c : ""}</p>
            <p className="tweet__text">{weatherStats ? weatherStats.current.temp_f: ""}</p>
            <p className="tweet__text">{weatherStats ? weatherStats.current.condition.text : ""}</p> */}

        </div>
    );
};

export default WeatherInfo;
