import React, { useState, useEffect } from "react";
import "./WeatherInfo.scss";
import { geolocation } from "react-geolocated";


const WeatherInfo = () => {
    const [weatherStats, setWeatherStats] = useState(null); //
    const [userLatitude, setUserLatitude] = useState(null);
    const [userLongitude, setUserLongitude] = useState(null);

    const successCallback = (position) => { 
        console.log(position);
        console.log(position.coords.latitude);
        console.log(position.coords.longitude);
        setUserLongitude(position.coords.longitude);
        setUserLatitude(position.coords.latitude);
    };

    const errorCallback = (error) => {
        console.error(error);
    };

    navigator.geolocation.getCurrentPosition(successCallback, errorCallback);

    const getWeatherStats = () => {
        // fetch("http://api.weatherapi.com/v1/current.json?key=e38763e617664e56ab8162340220102&q=London&aqi=no", { mode: 'no-cors'})
        fetch("http://api.weatherapi.com/v1/current.json?key=e38763e617664e56ab8162340220102&q=London&aqi=no")
            .then((response) => response.json()) 
            .then((jsonResponse) => setWeatherStats(jsonResponse)) 
            .catch((err) => console.log(err)); 
            // console.log(weatherStats.location.name);
    }

    useEffect(() => {
        getWeatherStats();
    }, []);

    return (
        <div>
            {/* <p className="">{weatherStats ? weatherStats.location.name : ""}</p> */}
